const Feature = require('app.feature');
const EventEmitter = require('events');

class Events extends Feature {
  static get config() {
    return {
      maxListeners: 10
    };
  }

  onCreate(done) {
    if (!this.api) {
      this.api = new EventEmitter;
    }

    this.api.setMaxListeners(this.config.maxListeners);

    super.onCreate(done);
  }
}

module.exports = Events;
